import React, { useState, useEffect } from 'react';
import imgl from '../asset/img/cart.jpg';
import del from '../asset/img/delete.jpg';
import edit from '../asset/img/edit.jpg';
import { Link, useNavigate } from 'react-router-dom';

function Dasboard() {
  const navigate = useNavigate();
  const [inputs, setInputs] = useState({});
  const [showData, setShowData] = useState(false);
  const [selectedExpense, setSelectedExpense] = useState(null); // To track the selected expense for editing or deleting
  const [modalOpen, setModalOpen] = useState(false); // To handle the modal visibility

  const myExpenses = [
    { id: 1, title: 'Expense 1', amount: 100, type: 'Cash', date: '2025-02-10' },
    { id: 2, title: 'Expense 2', amount: 200, type: 'Card', date: '2025-02-12' },
    // Add more expenses here for testing
  ];

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  useEffect(() => {
    // If needed, any side-effects can go here based on showData or selectedExpense
  }, [showData, selectedExpense]);

  const deleteExpense = () => {
    // Delete selected expense (you can replace this logic with actual deletion)
    console.log(`Deleted expense with id: ${selectedExpense.id}`);
    setModalOpen(false);
  };

  const openModal = (expense) => {
    setSelectedExpense(expense);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const editExpense = (event) => {
    event.preventDefault();
    console.log('Edited expense:', inputs);
    setShowData(true);
    setModalOpen(false);
  };

  const addExpense = (event) => {
    event.preventDefault();
    console.log('Added expense:', inputs);
    setShowData(true);
  };

  return (
    <div className="container">
      {showData ? (
        <div className="leftcontainer">
          <p>Total Expense: 1000</p>

          {/* Delete Confirmation Modal */}
          {modalOpen && selectedExpense && (
            <div id="myModal" className="modal">
              <div className="modal-content">
                <p>Do you want to delete this expense?</p>
                <button onClick={deleteExpense}>Yes</button>
                <button onClick={closeModal}>No</button>
              </div>
            </div>
          )}

          {/* Edit Expense Modal */}
          {modalOpen && selectedExpense && (
            <div id="myModal2" className="modal2">
              <div className="formcard">
                <h2 style={{ textAlign: 'center' }}>Edit Expense</h2>
                <form onSubmit={editExpense}>
                  <div>
                    <label>Title</label>
                    <input
                      required
                      type="text"
                      placeholder="Enter your expense title"
                      value={inputs.title || selectedExpense.title}
                      onChange={handleChange}
                      name="title"
                    />
                  </div>
                  <div>
                    <label>Amount</label>
                    <input
                      required
                      type="number"
                      placeholder="Enter your expense amount"
                      value={inputs.amount || selectedExpense.amount}
                      onChange={handleChange}
                      name="amount"
                    />
                  </div>
                  <div>
                    <label>Type</label>
                    <select
                      value={inputs.type || selectedExpense.type}
                      onChange={handleChange}
                      name="type"
                      required
                    >
                      <option disabled value="">
                        Please select payment type
                      </option>
                      <option value="cash">Cash</option>
                      <option value="upi">UPI</option>
                      <option value="card">Card</option>
                    </select>
                  </div>
                  <div>
                    <label>Date</label>
                    <input
                      required
                      type="date"
                      placeholder="Enter date"
                      value={inputs.date || selectedExpense.date}
                      onChange={handleChange}
                      name="date"
                    />
                  </div>
                  <div>
                    <button>Edit Expense</button>
                  </div>
                  <div>
                    <button type="button" onClick={closeModal}>
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}

          <div
            style={{
              backgroundColor: 'gray',
              width: '100%',
              overflowY: 'scroll',
              marginBottom: '50px',
              padding: '20px',
            }}
          >
            {myExpenses.map((expense) => (
              <div className="expensecard" key={expense.id}>
                <div
                  style={{
                    width: '100%',
                    display: 'flex',
                    justifyContent: 'flex-end',
                    alignItems: 'center',
                  }}
                >
                  <img
                    onClick={() => openModal(expense)}
                    style={{ width: '30px', height: '30px' }}
                    src={edit}
                    alt="Edit"
                  />
                  <img
                    onClick={() => openModal(expense)}
                    style={{ width: '20px', height: '20px', marginLeft: '4px' }}
                    src={del}
                    alt="Delete"
                  />
                </div>
                <p>{expense.title}</p>
                <p>{expense.amount}</p>
                <p>{expense.date}</p>
                <p>{expense.type}</p>
              </div>
            ))}
          </div>
        </div>
      ) : (
        <div className="leftcontainer">
          <p>Please add your expenses</p>
          <img src={imgl} alt="Cart" />
        </div>
      )}

      <div className="rightcontainer">
        <div className="formcard">
          <center>
            <h2>Add Expense</h2>
          </center>
          <form onSubmit={addExpense}>
            <div>
              <label>Title</label>
              <input
                required
                type="text"
                placeholder="Enter your expense title"
                value={inputs.title || ''}
                onChange={handleChange}
                name="title"
              />
            </div>
            <div>
              <label>Amount</label>
              <input
                required
                type="number"
                placeholder="Enter your expense amount"
                value={inputs.expense || ''}
                onChange={handleChange}
                name="expense"
              />
            </div>
            <div>
              <label>Type</label>
              <select
                value={inputs.type || ''}
                onChange={handleChange}
                name="type"
                required
              >
                <option disabled value="">
                  Please select payment type
                </option>
                <option value="cash">Cash</option>
                <option value="upi">UPI</option>
                <option value="card">Card</option>
              </select>
            </div>
            <div>
              <label>Date</label>
              <input
                required
                type="date"
                placeholder="Enter date"
                value={inputs.date || ''}
                onChange={handleChange}
                name="date"
              />
            </div>
            <div>
              <button type="submit">Add</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

